DELETE FROM "wand_cores";
INSERT INTO "wand_cores" ("id", "name", "category", "description", "available_for_random", "created_at", "category_sort_order") VALUES (29, 'Blána z dračího srdce', 'Tradiční', 'Mocné, temperamentní jádro, vhodné pro kouzelníky se silnou vůlí a odvahou. Skvělé pro bojové a transformační kouzla.', true, '2025-06-02T12:27:10.905Z', 1);
INSERT INTO "wand_cores" ("id", "name", "category", "description", "available_for_random", "created_at", "category_sort_order") VALUES (31, 'Pero fénixe', 'Tradiční', 'Nejpřizpůsobivější a nejtajemnější jádro. Umožňuje široké spektrum magie – včetně magií duše a smrti. Má silnou vlastní vůli.', true, '2025-06-02T12:27:10.905Z', 1);
INSERT INTO "wand_cores" ("id", "name", "category", "description", "available_for_random", "created_at", "category_sort_order") VALUES (30, 'Vlas z hřívy jednorožce', 'Tradiční', 'Velmi stabilní a spolehlivé jádro, výborné pro léčivá kouzla a čistou magii. Věrné a loajální svému původnímu majiteli.', true, '2025-06-02T12:27:10.905Z', 1);
INSERT INTO "wand_cores" ("id", "name", "category", "description", "available_for_random", "created_at", "category_sort_order") VALUES (32, 'Kořen mandragory (sušený, očarovaný)', 'Rostlinné', 'Silné spojení se zemí a životní silou. Vhodné pro léčitele a druidy.', false, '2025-06-02T12:27:10.905Z', 2);
INSERT INTO "wand_cores" ("id", "name", "category", "description", "available_for_random", "created_at", "category_sort_order") VALUES (33, 'Květ Asfodelu (uchovaný v kouzelnické pryskyřici)', 'Rostlinné', 'Vztah ke smrti a přechodu mezi světy. Vhodný pro nekromanty a spirituální mágy.', false, '2025-06-02T12:27:10.905Z', 2);
INSERT INTO "wand_cores" ("id", "name", "category", "description", "available_for_random", "created_at", "category_sort_order") VALUES (34, 'List měsíční kapradiny', 'Rostlinné', 'Posiluje iluze, neviditelnost, astrální projekci. Silné jádro pro mystiky a špehy.', false, '2025-06-02T12:27:10.905Z', 2);
INSERT INTO "wand_cores" ("id", "name", "category", "description", "available_for_random", "created_at", "category_sort_order") VALUES (37, 'Hadí jazyk (vzácný exemplář)', 'Tvorové', 'Vhodné pro hadomluvy, šepoty, temná zaklínadla a komunikaci se zvířaty.', false, '2025-06-02T12:27:10.905Z', 3);
INSERT INTO "wand_cores" ("id", "name", "category", "description", "available_for_random", "created_at", "category_sort_order") VALUES (36, 'Jed z akromantule (zakonzervovaný v vláknu)', 'Tvorové', 'Výborné pro subtilní, jedovatou magii – ovládání, iluze, temná alchymie.', false, '2025-06-02T12:27:10.905Z', 3);
INSERT INTO "wand_cores" ("id", "name", "category", "description", "available_for_random", "created_at", "category_sort_order") VALUES (38, 'Opeření z noční můry (stínového hippogryfa)', 'Tvorové', 'Posiluje kouzla spánku, vizí, nočních přeludů a astrální magie.', false, '2025-06-02T12:27:10.905Z', 3);
INSERT INTO "wand_cores" ("id", "name", "category", "description", "available_for_random", "created_at", "category_sort_order") VALUES (35, 'Zub vlkodlaka', 'Tvorové', 'Posiluje útočná kouzla, proměny a zvyšuje magickou agresi. Výborné pro temnou magii a kletby.', false, '2025-06-02T12:27:10.905Z', 3);
INSERT INTO "wand_cores" ("id", "name", "category", "description", "available_for_random", "created_at", "category_sort_order") VALUES (39, 'Dračí kámen (Bloodstone)', 'Minerální', 'Vztah k oběti a krvi. Vhodný pro rituální magii, léčitelství i prokletí.', false, '2025-06-02T12:27:10.905Z', 4);
INSERT INTO "wand_cores" ("id", "name", "category", "description", "available_for_random", "created_at", "category_sort_order") VALUES (41, 'Měsíční kámen', 'Minerální', 'Posiluje ženskou magii, věštění, vodní a lunární kouzla.', false, '2025-06-02T12:27:10.905Z', 4);
INSERT INTO "wand_cores" ("id", "name", "category", "description", "available_for_random", "created_at", "category_sort_order") VALUES (40, 'Obsidián s runovým leptem', 'Minerální', 'Skvělý pro magii štítů, run, ochranných kleteb. Pevný, spolehlivý.', false, '2025-06-02T12:27:10.905Z', 4);
INSERT INTO "wand_cores" ("id", "name", "category", "description", "available_for_random", "created_at", "category_sort_order") VALUES (42, 'Rudý jantar s duší hmyzího krále (alchymický artefakt)', 'Minerální', 'Podporuje experimentální magii a alchymii. Hůlka se „učí" s majitelem.', false, '2025-06-02T12:27:10.905Z', 4);
INSERT INTO "wand_cores" ("id", "name", "category", "description", "available_for_random", "created_at", "category_sort_order") VALUES (43, 'Vlas víly', 'Temnější', 'Na první pohled krásné a třpytivé, ale jádro je nestálé, nevyzpytatelné a často povrchní. Kouzla bývají efektní, ale málo stabilní.', false, '2025-06-02T12:27:29.922Z', 5);
INSERT INTO "wand_cores" ("id", "name", "category", "description", "available_for_random", "created_at", "category_sort_order") VALUES (49, 'Šupina černé mantikory', 'Temnější', 'Nebezpečné jádro pro silně destruktivní magii – výbuchy, jedy, smrtící pasti. Oblíbené mezi zabijáky a temnými alchymisty.', false, '2025-06-02T12:27:29.922Z', 5);
INSERT INTO "wand_cores" ("id", "name", "category", "description", "available_for_random", "created_at", "category_sort_order") VALUES (46, 'Krystalizovaná krev harpyje', 'Temnější', 'Posiluje emoce, rozvíjí kouzla založená na chtíči, hněvu, bolesti. Silné v temné magii a při obětních rituálech.', false, '2025-06-02T12:27:29.922Z', 5);
INSERT INTO "wand_cores" ("id", "name", "category", "description", "available_for_random", "created_at", "category_sort_order") VALUES (44, 'Nehet ďasovce', 'Temnější', 'Brutální a primitivní magie, často založená na síle a agresi. Jádro oblíbené u černokněžníků, kteří spoléhají na fyzickou destrukci.', false, '2025-06-02T12:27:29.922Z', 5);
INSERT INTO "wand_cores" ("id", "name", "category", "description", "available_for_random", "created_at", "category_sort_order") VALUES (45, 'Oko ghúla (zdeformované, zakleté)', 'Temnější', 'Oko má schopnost „vidět za roh", posiluje detekční a špehovací kouzla. Výborné pro černou špionáž.', false, '2025-06-02T12:27:29.922Z', 5);
INSERT INTO "wand_cores" ("id", "name", "category", "description", "available_for_random", "created_at", "category_sort_order") VALUES (47, 'Peří z upířího netopýra', 'Temnější', 'Dobré pro magii stínů, ticha, únikových kouzel a noční manipulace. Mistr v magickém skrývání.', false, '2025-06-02T12:27:29.922Z', 5);
INSERT INTO "wand_cores" ("id", "name", "category", "description", "available_for_random", "created_at", "category_sort_order") VALUES (48, 'Slina banshee (vysušená v tkanině smrti)', 'Temnější', 'Výjimečné pro kouzla smutku, bolesti, zaklínání duchů a smrtelných předtuch.', false, '2025-06-02T12:27:29.922Z', 5);
INSERT INTO "wand_cores" ("id", "name", "category", "description", "available_for_random", "created_at", "category_sort_order") VALUES (50, 'Vlákno mozkomora (extrémně zakázané)', 'Temnější', 'Temné jádro schopné manipulovat se vzpomínkami a vůlí jiných. Vhodné pro mistrné ovládání myslí a bezcitnou černou magii.', false, '2025-06-02T12:27:29.922Z', 5);

-- Table: wand_lengths
DELETE FROM "wand_lengths";
INSERT INTO "wand_lengths" ("id", "name", "description", "available_for_random", "created_at", "sort_order") VALUES (26, '7"', 'Krátká, vhodná pro precizní, rychlé kouzlení. Často u velmi mladých čarodějů nebo osob s přesnou kontrolou.', true, '2025-06-02T12:30:11.340Z', 1);
INSERT INTO "wand_lengths" ("id", "name", "description", "available_for_random", "created_at", "sort_order") VALUES (27, '8"', 'Lehce podprůměrná, oblíbená u diskrétních, taktických kouzelníků.', true, '2025-06-02T12:30:11.340Z', 2);
INSERT INTO "wand_lengths" ("id", "name", "description", "available_for_random", "created_at", "sort_order") VALUES (28, '9"', 'Neutrální délka, snadno ovladatelná – vhodná pro většinu uživatelů.', true, '2025-06-02T12:30:11.340Z', 3);
INSERT INTO "wand_lengths" ("id", "name", "description", "available_for_random", "created_at", "sort_order") VALUES (29, '10"', 'Běžná délka. Vyvážená hůlka pro různorodé účely.', true, '2025-06-02T12:30:11.340Z', 4);
INSERT INTO "wand_lengths" ("id", "name", "description", "available_for_random", "created_at", "sort_order") VALUES (30, '11"', 'Mírně delší, ideální pro čaroděje se silným charakterem nebo rozsáhlou magickou silou.', true, '2025-06-02T12:30:11.340Z', 5);
INSERT INTO "wand_lengths" ("id", "name", "description", "available_for_random", "created_at", "sort_order") VALUES (31, '12"', 'Výrazná hůlka, častá u mágů s dominantní povahou nebo hlubokým nitrem.', true, '2025-06-02T12:30:11.340Z', 6);
INSERT INTO "wand_lengths" ("id", "name", "description", "available_for_random", "created_at", "sort_order") VALUES (32, '13"', 'Pro ty, kteří mají neobyčejný potenciál nebo extrémní specializaci.', false, '2025-06-02T12:30:11.340Z', 7);
INSERT INTO "wand_lengths" ("id", "name", "description", "available_for_random", "created_at", "sort_order") VALUES (33, '14"', 'Dlouhá hůlka, vhodná pro formální, velkolepou nebo rituální magii.', false, '2025-06-02T12:30:11.340Z', 8);
INSERT INTO "wand_lengths" ("id", "name", "description", "available_for_random", "created_at", "sort_order") VALUES (34, '15"', 'Rarita – vyžaduje silné zaměření, ale odmění velkým dosahem a účinkem.', false, '2025-06-02T12:30:11.340Z', 9);
INSERT INTO "wand_lengths" ("id", "name", "description", "available_for_random", "created_at", "sort_order") VALUES (35, '16+"', 'Neobvyklá až výstřední délka. Obvykle jen u obrů, divotvůrců nebo výjimečných osobností.', false, '2025-06-02T12:30:11.340Z', 10);

-- Table: wand_flexibilities
DELETE FROM "wand_flexibilities";
INSERT INTO "wand_flexibilities" ("id", "name", "description", "available_for_random", "created_at") VALUES (28, 'Výjimečně poddajná', 'Až nezvyklá měkkost, ale schopnost reagovat na jemné impulzy.', true, '2025-06-02T12:28:45.836Z');
INSERT INTO "wand_flexibilities" ("id", "name", "description", "available_for_random", "created_at") VALUES (22, 'Mírně nepoddajná', 'Stále tvrdá, ale snese kompromis s podobně silnou osobností.', true, '2025-06-02T12:28:45.836Z');
INSERT INTO "wand_flexibilities" ("id", "name", "description", "available_for_random", "created_at") VALUES (21, 'Nepoddajná', 'Odolná změnám, vhodná pro konzervativní nebo zásadové čaroděje.', true, '2025-06-02T12:28:45.836Z');
INSERT INTO "wand_flexibilities" ("id", "name", "description", "available_for_random", "created_at") VALUES (19, 'Nezlomná', 'Extrémně vzácná, silně spojená s jediným majitelem. Věrná do posledního dechu.', true, '2025-06-02T12:28:45.836Z');
INSERT INTO "wand_flexibilities" ("id", "name", "description", "available_for_random", "created_at") VALUES (25, 'Ohebná', 'Schopná adaptace, oblíbená u čarodějů, kteří se vyvíjejí nebo přizpůsobují.', true, '2025-06-02T12:28:45.836Z');
INSERT INTO "wand_flexibilities" ("id", "name", "description", "available_for_random", "created_at") VALUES (23, 'Pevná', 'Spolehlivá, stabilní, dobrá volba pro vyrovnané a disciplinované čaroděje.', true, '2025-06-02T12:28:45.836Z');
INSERT INTO "wand_flexibilities" ("id", "name", "description", "available_for_random", "created_at") VALUES (26, 'Pružná', 'Výborná volba pro flexibilní, vynalézavé nebo empatické osobnosti.', true, '2025-06-02T12:28:45.836Z');
INSERT INTO "wand_flexibilities" ("id", "name", "description", "available_for_random", "created_at") VALUES (24, 'Tvrdá', 'Lehce nepružná, ale funkční, vhodná pro magii vyžadující pevnou ruku.', true, '2025-06-02T12:28:45.836Z');
INSERT INTO "wand_flexibilities" ("id", "name", "description", "available_for_random", "created_at") VALUES (20, 'Velmi nepoddajná', 'Silně tvrdohlavá, pracuje pouze s dominantní osobností nebo silnou vůlí.', true, '2025-06-02T12:28:45.836Z');
INSERT INTO "wand_flexibilities" ("id", "name", "description", "available_for_random", "created_at") VALUES (27, 'Velmi pružná', 'Rychle se přizpůsobuje, ideální pro chaotické nebo nevyzpytatelné mágy.', true, '2025-06-02T12:28:45.836Z');
INSERT INTO "wand_flexibilities" ("id", "name", "description", "available_for_random", "created_at") VALUES (29, 'Vrbovitá', 'Metaforická i fyzická označení pro hůlku, která „tancuje" s energií uživatele – častá u empatů a intuitivních kouzelníků.', true, '2025-06-02T12:28:45.836Z');

