INSERT INTO "wands" ("id", "character_id", "wood", "core", "length", "flexibility", "description", "acquired_at") VALUES (10, 15, 'Vinná réva', 'Kořen mandragory (sušený, očarovaný)', '7"', 'Nezlomná', 'Nejedná se vůbec o hůlku jako takovou, nýbrž o totem. Podobně jako hůlka se skládá ze dvou částí. Jednou je klasická ergonomická rukojeť s protiskluzovým vyřezáváním. Z rukojeti však místo hrotu vyskakuje vyřezávaný gepard.', '2025-06-08T15:41:55.997Z');
INSERT INTO "wands" ("id", "character_id", "wood", "core", "length", "flexibility", "description", "acquired_at") VALUES (11, 19, 'Červený dub', 'Pero fénixe', '8"', 'Výjimečně poddajná', 'Hůlka z červený dub, 8" dlouhá, výjimečně poddajná, s jádrem pero fénixe. Vybrána Ollivanderem osobně pro svého nového majitele.', '2025-06-08T15:46:32.215Z');

